mapper.sql:
FROM
(select * from tz.t_tz_priority_back_pool a where 1=1 ${dataRights} ) a
tz.t_tz_priority_back_pool a
LEFT JOIN ( SELECT priority_id, MAX(check_time) chktm FROM tz.t_tz_priority_back_log WHERE check_time IS NOT NULL GROUP BY priority_id ) x ON x.priority_id = a.priority_id LEFT JOIN tz.t_tz_priority_back_log pbl ON pbl.priority_id = x.priority_id AND pbl.check_time = x.chktm LEFT JOIN tz.t_tz_loan_apply loan ON a.lend_code = loan.lend_code LEFT JOIN tz.t_tz_back_money_pool pool ON pool.lend_code=loan.lend_code LEFT JOIN tz.t_gl_tz_product p ON p.product_code=loan.product_code LEFT JOIN tz.t_gl_org org ON org.id=loan.store_org_id LEFT JOIN tz.t_gl_province_city pc ON pc.id=org.city_id LEFT JOIN tz.t_gl_user usr ON usr.id=loan.manager_code LEFT JOIN tz.t_tz_customer_account acc ON acc.id=loan.receiving_id LEFT JOIN tz.t_gl_province_city pc1 ON pc1.id=acc.account_addrprovince LEFT JOIN tz.t_gl_province_city pc2 ON pc2.id=acc.account_addrcity LEFT JOIN tz.t_tz_customer cust ON cust.customer_code=loan.customer_code
pbl.create_by = #{userId}
AND a.priority_id IN
#{item}
AND
cust.customer_name like '%${item}%'
AND
cust.customer_code like '%${item}%'
AND
loan.lend_code like '%${item}%'
AND loan.store_org_id IN
'${item}'
AND loan.apply_lend_day >= #{applyLendDay}
AND loan.apply_lend_day <= #{applyLendDayTo}
AND loan.apply_pay IN
'${item}'
AND loan.apply_lend_money >= #{applyLendMoney}
AND loan.apply_lend_money <= #{applyLendMoneyTo}
AND loan.product_code IN
'${item}'
AND apply_expire_day >= #{applyExpireDay}
AND apply_expire_day <= #{applyExpireDayTo}
AND pool.back_money_day >= #{backMoneyDay}
AND pool.back_money_day <= #{backMoneyDayTo}
AND loan.approve_date >= #{approveDateStart}
AND loan.approve_date <= #{approveDateEnd}
AND acc.account_addrprovince||'_'||acc.account_addrcity SIMILAR TO #{city}
AND acc.account_card_or_booklet IN
'${item}'
AND loan.apply_agreement_edition IN
'${item}'
AND usr.name = #{manager}
AND a.priority_back_state IN
'${item}'
